      "descrizione": "<blockquote>\r\n<div><big><span style=\"font-size: medium;\"><span style=\"font-size: 14px; line-height: 1.5em;\">\u00a0<\/span><\/span><\/big><\/div>\r\n<div><a href=\"http:\/\/www.frascatiscienza.it\/wp-content\/uploads\/2014\/03\/adone.jpg\"><img class=\"size-full wp-image-12587 aligncenter\" alt=\"adone\" src=\"http:\/\/www.frascatiscienza.it\/wp-content\/uploads\/2014\/03\/adone.jpg\" width=\"515\" height=\"662\" \/><\/a><\/div><\/blockquote>",
